mapper.select:
    		SELECT QAN_NUM, QAN_DATE, QAN_TITLE, QAN_STATUS
			FROM QAN_BOARD
			WHERE USER_NUM=#{userNum}

    		SELECT QAN_DATE, QAN_STATUS, QAN_TITLE, QAN_CONTENT, QAN_FILE, QAN_ANSWER
			FROM QAN_BOARD
			WHERE QAN_NUM=#{qnaNum}

mapper.update:
    	UPDATE QAN_BOARD
		SET QAN_STATUS='완료', QAN_ANSWER=#{qnaAnswer}
		WHERE QAN_NUM=#{qnaNum}
